mapper.select:
SELECT t.TETEIL,t.TEERZEUGNIS,a.ALARM_EW,a.ALARM_DATETIME FROM TEIL t 
LEFT JOIN ALARM_VALUES a ON t.TETEIL= a.K1000 
where 1=1

	and TEERZEUGNIS like concat ('%',#{productLineName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

select t.TETEIL,t.TETEILNR,t.TEBEZEICH,a.ALARM_EW,a.ALARM_DATETIME from TEIL t
LEFT JOIN ALARM_VALUES a ON t.TETEIL= a.K1000 
where 1=1 and TEERZEUGNIS = #{productLineName}

	and TETEILNR like concat ('%',#{teilNum},'%')


	and TEBEZEICH like concat ('%',#{teilName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

SELECT m.METEIL,m.MEMERKMAL,m.MEMERKBEZ,a.ALARM_EW,a.ALARM_DATETIME FROM MERKMAL m
LEFT JOIN ALARM_VALUES a ON m.METEIL= a.K1000 
where m.METEIL=#{teilId}

	and MEMERKBEZ like concat ('%',#{merkmalName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

select w.WVTEIL,w.WVMERKMAL,w.WVWERTNR,w.WVWERT,w.WVPRUEFER,w.WVPRUEFMIT,w.WVMASCHINE,w.WVNEST,w.WVDATZEIT,m.MEUGW,m.MEOGW
FROM WERTEVAR w
LEFT JOIN MERKMAL m ON w.WVTEIL=m.METEIL AND w.WVMERKMAL=m.MEMERKMAL
where WVMERKMAL=#{merkmalId} and WVTEIL=#{teilId}

	and w.WVDATZEIT > #{startTime}


	and w.WVDATZEIT < #{endTime}

select w.WVTEIL,w.WVMERKMAL,w.WVWERTNR,w.WVWERT,w.WVPRUEFER,w.WVPRUEFMIT,w.WVMASCHINE,w.WVNEST,w.WVDATZEIT,
p.PRVORNAME,
r.PMNR,r.PMBEZ,
m.MEUGW,m.MEOGW
from WERTEVAR w 
LEFT JOIN PRUEFER p ON CONCAT(w.WVPRUEFER,'')=p.PRNAME 
LEFT JOIN PRUEFMIT r ON w.WVPRUEFMIT = r.PMPRUEFMIT
LEFT JOIN MERKMAL m ON W.WVTEIL=m.METEIL AND w.WVMERKMAL=m.MEMERKMAL
where WVMERKMAL=#{merkmalId} and WVTEIL=#{teilId}

	and WVDATZEIT > #{startTime}


	and WVDATZEIT < #{endTime}


	order by ${sort} ${order}
