mapper.select:
SELECT t.TETEIL,t.TEERZEUGNIS,a.ALARM_EW,a.ALARM_DATETIME FROM TEIL t 
LEFT JOIN ALARM_VALUES a ON t.TETEIL= a.K1000 
where 1=1

	and TEERZEUGNIS like concat ('%',#{productLineName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

select t.TETEIL,t.TETEILNR,t.TEBEZEICH,a.ALARM_EW,a.ALARM_DATETIME from TEIL t
LEFT JOIN ALARM_VALUES a ON t.TETEIL= a.K1000 
where 1=1 and TEERZEUGNIS = #{productLineName}

	and TETEILNR like concat ('%',#{teilNum},'%')


	and TEBEZEICH like concat ('%',#{teilName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

SELECT m.METEIL,m.MEMERKMAL,m.MEMERKBEZ,a.ALARM_EW,a.ALARM_DATETIME FROM MERKMAL m
LEFT JOIN ALARM_VALUES a ON m.METEIL= a.K1000 AND m.MEMERKMAL=a.K2000
where m.METEIL=#{teilId}

	and MEMERKBEZ like concat ('%',#{merkmalName},'%')


	and a.ALARM_DATETIME > #{startTime}


	and a.ALARM_DATETIME < #{endTime}

select w.WVTEIL,w.WVMERKMAL,w.WVWERTNR,w.WVWERT,w.WVDATZEIT,m.MEUGW,m.MEOGW,m.MENENNMAS,m.MEMERKART
FROM WERTEVAR w
LEFT JOIN MERKMAL m ON w.WVTEIL=m.METEIL AND w.WVMERKMAL=m.MEMERKMAL
where WVMERKMAL=#{merkmalId} and WVTEIL=#{teilId}

	and w.WVDATZEIT > #{startTime}


	and w.WVDATZEIT < #{endTime}

select w.WVTEIL,w.WVMERKMAL,w.WVWERTNR,w.WVWERT,w.WVPRUEFER,w.WVPRUEFMIT,w.WVMASCHINE,w.WVNEST,w.WVDATZEIT,
p.PRVORNAME,
r.PMNR,r.PMBEZ,
m.MEUGW,m.MEOGW
from WERTEVAR w 
LEFT JOIN PRUEFER p ON CONCAT(w.WVPRUEFER,'')=p.PRNAME 
LEFT JOIN PRUEFMIT r ON w.WVPRUEFMIT = r.PMPRUEFMIT
LEFT JOIN MERKMAL m ON W.WVTEIL=m.METEIL AND w.WVMERKMAL=m.MEMERKMAL
where WVMERKMAL=#{merkmalId} and WVTEIL=#{teilId}

	and w.WVDATZEIT > #{startTime}


	and w.WVDATZEIT < #{endTime}


	order by ${sort} ${order}

select p.permission_id,p.permission_name,p.type, t.TETEIL,t.TETEILNR,t.TEBEZEICH,t.TEERZEUGNIS, a.ALARM_EW,a.ALARM_DATETIME from system_permission p LEFT JOIN Teil t ON t.TEERZEUGNIS=p.permission_name LEFT JOIN ALARM_VALUES a ON t.TETEIL=a.K1000 where p.permission_name in
#{item}
and p.available='0' and p.type='pl'
and a.ALARM_DATETIME > #{startTime}
and a.ALARM_DATETIME < #{endTime}

SELECT t.TETEIL,t.TETEILNR,t.TEBEZEICH,t.TEERZEUGNIS, m.MEMERKMAL,m.MEMERKBEZ,m.MENENNMAS,m.MEMERKART,m.MEUGW,m.MEOGW, COUNT (w.WVDATZEIT) AS wercount FROM TEIL t LEFT JOIN MERKMAL m ON t.TETEIL=m.METEIL LEFT JOIN WERTEVAR w ON t.TETEIL = w.WVTEIL AND m.MEMERKMAL = w.WVMERKMAL WHERE t.TEERZEUGNIS IN
#{item}
and w.WVDATZEIT > #{startTime}
and w.WVDATZEIT < #{endTime}
GROUP BY t.TETEIL,t.TETEILNR,t.TEBEZEICH,t.TEERZEUGNIS,m.MEMERKMAL,m.MEMERKBEZ,m.MENENNMAS,m.MEMERKART,m.MEUGW,m.MEOGW ORDER BY t.TEERZEUGNIS,t.TETEIL,m.MEMERKMAL

SELECT t.TETEIL,t.TETEILNR,t.TEBEZEICH,t.TEERZEUGNIS, m.MEMERKMAL,m.MEMERKBEZ,m.MENENNMAS,m.MEMERKART,m.MEUGW,m.MEOGW, COUNT (w.WVDATZEIT) AS wercount FROM TEIL t LEFT JOIN MERKMAL m ON t.TETEIL=m.METEIL LEFT JOIN WERTEVAR w ON t.TETEIL = w.WVTEIL AND m.MEMERKMAL = w.WVMERKMAL WHERE t.TETEIL=#{teilId}
and w.WVDATZEIT > #{startTime}
and w.WVDATZEIT < #{endTime}
GROUP BY t.TETEIL,t.TETEILNR,t.TEBEZEICH,t.TEERZEUGNIS,m.MEMERKMAL,m.MEMERKBEZ,m.MENENNMAS,m.MEMERKART,m.MEUGW,m.MEOGW ORDER BY t.TEERZEUGNIS,t.TETEIL,m.MEMERKMAL
